import './style/App.css';
import reviews from './Data';
import React, {useState, useEffect} from 'react';
import {FaQuoteRight} from 'react-icons/fa';
import {BsStarFill, BsStarHalf, BsStar} from 'react-icons/bs';
import {MdKeyboardArrowRight, MdKeyboardArrowLeft} from 'react-icons/md'

function App() {
  const [Reviews, setReviews] = useState(reviews);
  const [Id, setId] = useState(0);
  var rating = Reviews[Id].rating;
  const [click, setClick] = useState();
  useEffect(() => {
    (rating % 1) ? setClick(0)
      : setClick(2)
  }, [rating])
  function randomize(exclusion){
    var random = Math.ceil(Math.random() * Reviews.length) - 1;
    return (random !== exclusion) ? random : randomize(exclusion);
  }
  function rate(starNum){
    starNum++;
    var {id, name, job, image, text} = Reviews[Id];
    if(starNum === Math.floor(rating)){
      (starNum % 1) ? setReviews(Reviews.map(review => review = (review.id !== id) ? review : {id, rating: starNum - 1, name, job, image, text})) : setReviews(Reviews.map(review => review = (review.id !== id) ? review : {id, rating: starNum - 0.5, name, job, image, text}));
    }
    else{
      setReviews(Reviews.map(review => review = (review.id !== id) ? review : {id, rating: starNum, name, job, image, text}));
    }
  }
  function stars(){
    var stars = [];
    function setStar(i){
      stars[i] = (rating > i) ? (rating - i === 0.5)
      // eslint-disable-next-line
        ? <BsStarHalf key={i} onClick={()=>rate(i)}/> : <BsStarFill key={i} onClick={()=>rate(i)}/>
      // eslint-disable-next-line
        : <BsStar key={i} onClick={()=>rate(i)}/>; 
    }
    setStar(0);
    setStar(1);
    setStar(2);
    setStar(3);
    setStar(4);
    return stars;
  }
  return (
    <div className="App">
      <h3 className='quote'><FaQuoteRight/></h3>
      <div>
        <img src={Reviews[Id].image} alt='ï¿½'/>
      </div>
      <h3>{`${(Reviews[Id].name).split(' ')[0].charAt(0).toUpperCase()}${(Reviews[Id].name).split(' ')[0].slice(1)} ${(Reviews[Id].name).split(' ')[1].charAt(0).toUpperCase()}${(Reviews[Id].name).split(' ')[1].slice(1)}`}</h3>
      <p className='job'>{(Reviews[Id].job).toUpperCase()}</p>
      <p className='para'>{Reviews[Id].text}</p>
      <h2 className='rating'>{
        stars()
      }</h2>
      <h1 className='arrows'><MdKeyboardArrowLeft onClick={()=>{setId((Id - 1 === -1) ? Reviews.length - 1 : Id - 1)}}/><MdKeyboardArrowRight onClick={()=>{setId((Id + 1 === Reviews.length) ? 0 : Id + 1)}}/></h1>
      <button onClick={()=>setId(randomize(Id))}>Surprise me</button>
    </div>
  );
}

export default App;
