import './style/App.css';
import reviews from './Data';
import React, {useState} from 'react';
import {FaQuoteRight} from 'react-icons/fa';
import {MdKeyboardArrowRight, MdKeyboardArrowLeft} from 'react-icons/md'

function App() {
  const [Reviews] = useState(reviews);
  const [id, setId] = useState(0);
  function random(){
    return Math.floor(Math.random() * (Reviews.length - 1)) + 1;
  }
  return (
    <div className="App">
      <h3 className='quote'><FaQuoteRight/></h3>
      <div>
        <img src={Reviews[id].image} alt='ï¿½'/>
      </div>
      <h3>{`${(Reviews[id].name).split(' ')[0].charAt(0).toUpperCase()}${(Reviews[id].name).split(' ')[0].slice(1)} ${(Reviews[id].name).split(' ')[1].charAt(0).toUpperCase()}${(Reviews[id].name).split(' ')[1].slice(1)}`}</h3>
      <p className='job'>{(Reviews[id].job).toUpperCase()}</p>
      <p className='para'>{Reviews[id].text}</p>
      <h1 className='arrows'><MdKeyboardArrowLeft onClick={()=>{setId((id - 1 === -1) ? Reviews.length - 1 : id - 1)}}/><MdKeyboardArrowRight onClick={()=>{setId((id + 1 === Reviews.length) ? 0 : id + 1)}}/></h1>
      <button onClick={()=>setId((random !== id) ? random : random())}>Surprise me</button>
    </div>
  );
}

export default App;
